    public static final int DIAGONAL_COST = 114;
    public static final int VERTICAL_HORIZONTAL_COST = 100;
    public static final int ROBOT_WIDTH = 9; //9*2 = 18
    public static final int ROBOT_HEIGHT = 9; //9*2 = 18

    private Cell[][] grid;

    private PriorityQueue<Cell> openCells;
    private boolean[][] closedCells;

    private int startX, startY;
    private int endX, endY;

    private int heuristicApproximation(int x, int y, int eX, int eY) {
        //Euclidean.
        //return (x-eX)*(x-eX) + (y-eY)*(y-eY);
        //Dijkstras.
        return 0;
    }

    public AStarAlgorithmm(int sx, int sy, int ex, int ey) {
        grid = new Cell[mappedFTCGrid.length][mappedFTCGrid[0].length];
        closedCells = new boolean[mappedFTCGrid.length][mappedFTCGrid[0].length];
        openCells = new PriorityQueue<Cell>(72, new CellComparator());
        startX = sx;
        startY = sy;
        endX = ex;
        endY = ey;

        //init heuristic and cells
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                if (mappedFTCGrid[i][j] == 1) {
                    grid[i][j] = null;
                } else {
                    grid[i][j] = new Cell(i, j);
                    grid[i][j].heuristicCost = heuristicApproximation(i, j, ex, ey);
                    grid[i][j].solution = false;
                }
            }
        }

        grid[startX][startY].finalCost = 0;
    }

    private boolean is18x18SquareValid(Cell cell) {
        for (int i = cell.x - 5; i <= cell.x + 5; i++) {
            for (int j = cell.y - 5; j <= cell.y + 5; j++) {
                if (!isValidCell(i, j) || grid[i][j] == null) {
                    return false;
                }
            }
        }
        // int[][] ops = {
        //     {-1,-1},{1,-1},{1,1},{-1,1}
        // };
        // for (int[] op : ops) {
        //     int oX = cell.x+op[0]*5;
        //     int oY = cell.y+op[1]*5;
        //     if (!isValidCell(oX, oY) || grid[oX][oY]==null) {
        //         return false;
        //     }
        // }
        return true;
    }

    private void updateCostIfNeeded(Cell current, Cell t, int cost) {
        if (t == null || closedCells[t.x][t.y] || !is18x18SquareValid(t)) {
            return;
        }

        int tFinalCost = t.heuristicCost + cost;
        boolean isOpen = openCells.contains(t);

        if (!isOpen || tFinalCost < t.finalCost) {
            t.finalCost = tFinalCost;
            t.parent = current;

            if (!isOpen) {
                openCells.add(t);
            }
        }
    }

    private boolean isValidCell(int x, int y) {
        return x >= 0 && y >= 0 && x < grid.length && y < grid[0].length;
    }

    public void process() {
        openCells.add(grid[startX][startY]);
        Cell current;

        while (true) {
            current = openCells.poll();

            if (current == null) {
                break;
            }

            closedCells[current.x][current.y] = true;
            if (current.equals(grid[endX][endY])) {
                return;
            }

            Cell t;
            int operations[][] = {
                    {-1, -1, DIAGONAL_COST}, {0, -1, VERTICAL_HORIZONTAL_COST}, {1, -1, DIAGONAL_COST}, {1, 0, VERTICAL_HORIZONTAL_COST}, {1, 1, DIAGONAL_COST}, {0, 1, VERTICAL_HORIZONTAL_COST}, {-1, 1, DIAGONAL_COST}, {-1, 0, VERTICAL_HORIZONTAL_COST}
            };
            for (int[] ops : operations) {
                int x = current.x + ops[0];
                int y = current.y + ops[1];
                int movementCost = ops[2];

                if (isValidCell(x, y)) {
                    t = grid[x][y];
                    updateCostIfNeeded(current, t, current.finalCost + movementCost);
                }
            }
        }
    }

    public void displayScores() {
        System.out.println("\nScores for cells :");
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                if (grid[i][j] != null) {
                    System.out.printf("%-3d ", grid[i][j].finalCost);
                } else {
                    System.out.print("BL ");
                }
            }
            System.out.println();
        }
        System.out.println();
    }

